mapper.insert:


					select seq_dk_chat_room.nextval from dual


 			insert into dk_chat_room (	  chatRoomNum
							 			, roomOwnerId
							 			, roomOwnerNick
							 			, chat_type
							 			, headCount
					 			   )
					 		values(
						 				 #{chatRoomNum}
										,#{roomOwnerId}
										,#{roomOwnerNick}
										,#{chat_type}				 		
										,#{headCount}				 		
					 				)

 			insert into dk_chat_member (	  chatRoomNum
								 			, chat_memberId
								 			, chat_memberNick
						 			   )
						 		values(
							 				 #{chatRoomNum}
											,#{chat_memberId}
											,#{chat_memberNick}
						 				)

mapper.select:
select content.chatContentNum , chat_writerId , chat_writerNick , chat_content , content_type , regdate , readCount , read.read_type
from dk_chat_content content LEFT OUTER JOIN dk_chat_read read ON content.chatContentNum = read.chatContentNum where content.chatRoomNum = #{chatRoomNum}
and content.regdate > #{recentOutDate}
and read.chat_memberId = #{chat_memberId} order by content.chatContentNum asc

mapper.insert:


				select seq_dk_chat_content.nextval from dual


 			insert into dk_chat_content (	  chatContentNum
								 			, chatRoomNum
								 			, chat_content
								 			, chat_writerId
								 			, chat_writerNick
								 			, regDate
								 			, readCount
						 			   )
						 		values(
							 				 #{chatContentNum}
											,#{chatRoomNum}
											,#{chat_content}
											,#{chat_writerId}
											,#{chat_writerNick}
											,#{regDate}
											,#{readCount}
						 				)



					select seq_dk_chat_content.nextval from dual


 			insert into dk_chat_content (	  chatContentNum
								 			, chatRoomNum
								 			, chat_content
								 			, regDate
	 										, content_type
						 			   )
						 		values(
							 				 #{chatContentNum}
											,#{chatRoomNum}
											,#{chat_content}
											,#{regDate}
											, 1
						 				)

 			insert into dk_chat_read (	  	  chatReadNum
 											, chatRoomNum
								 			, chatContentNum
								 			, chat_memberId
						 			   )
						 		values(
							 				 seq_dk_chat_read.nextval
							 				, #{chatRoomNum}
											, #{chatContentNum}
											, #{chat_memberId}
						 				)
