mapper.delete:
delete from dk_alarm where target = #{target} 
					and commonVar1 = #{commonVar1} 
					and commonVar2 = #{commonVar2} 
					and kind = #{kind}
					and writerNick = #{writerNick}
					and writerId = #{writerId}

mapper.select:
select NOTE_NUM , CONTENT , TO_NICKNAME , TO_ID , read_check , regdate
from (select /*+INDEX_DESC(DK_NOTE PK_DK_NOTE) */ rownum rn , NOTE_NUM , CONTENT , TO_NICKNAME , TO_ID , read_check , regdate
from dk_note where from_id = #{userId} and to_id != #{userId} and to_check = 'NO' and rownum <= #{pageNum} * #{amount} ) where rn > (#{pageNum} -1) * #{amount}

mapper.insert:
 			insert into dk_note(	  NOTE_NUM
						 			, CONTENT
						 			, FROM_NICKNAME
						 			, FROM_ID
						 			, TO_NICKNAME
						 			, TO_ID 
						 			, read_check
				 			   ) 
				 		values(
					 				seq_dk_note.nextval
					 				,#{content}
									,#{from_nickname}
									,#{from_id}
									,#{to_nickname}				 		
							 		,#{to_id}
							 		,#{read_check}
				 				)
