mapper.select:
select category , replyCnt , likeCnt , board_num , title , content , nickName , regdate , hitCnt , MONEY , userId from ( select
ROW_NUMBER() OVER(order by hitcnt desc) AS rn
ROW_NUMBER() OVER(order by REPLYCNT desc) AS rn
ROW_NUMBER() OVER(order by likeCnt desc) AS rn
ROW_NUMBER() OVER(order by MONEY desc) AS rn
, category , replyCnt , likeCnt , board_num , title , content , nickName , regdate , hitCnt , MONEY , userId from DK_BOARD where category = #{category}
)
where rn > (#{pageNum} -1) * #{amount} and rn <= #{pageNum} * #{amount}

mapper.insert:

		select seq_dk_board.nextval from dual


insert into DK_BOARD ( board_num
					 , category
					 , title
					 , content
					 , nickName
					 , userId
					 )
               values (  #{board_num}
		               , #{category}
		               , #{title}
		               , #{content}
		               , #{nickName}
		               , #{userId}
               )

insert into dk_board_like (   userId
							, board_num
							, likeValue
						  )
					values (  #{userId}
							, #{board_num}
							, 'push'
						   )

insert into dk_board_dislike(     userId
								, board_num
								, dislikeValue
							)
					 values (	   #{userId}
								 , #{board_num}
								 , 'push'
								 )

insert into dk_cash ( cash_num
					, cashKind
					, cashAmount
					, userId
					, nickName
					, specification
					, board_num
				) values 
						(
							  seq_dk_cash.nextval
							, '기부하기'
							, #{money}
							, #{userId}
							, #{nickName}
							, '게시판'
							, #{board_num}
						)

insert into dk_cash ( cash_num
					, cashKind
					, cashAmount
					, userId
					, nickName
					, specification
					, board_num
					)   
					 values 
							(
								seq_dk_cash.nextval
								, '기부받기'
								, #{money}
								, #{donatedId}
								, #{donatedNickName}
								, '게시판'
								, #{board_num}
							)

insert into dk_report ( 	report_num
						  , reportKind
						  , reportingId
						  , reportingNick
						  , reportedId
						  , reportedNick
						  , board_num
						  , reason
				      ) 
				      	values 
							(     seq_dk_report.nextval
								, #{reportKind}
								, #{reportingId}
								, #{reportingNick}
								, #{reportedId}
								, #{reportedNick}
								, #{board_num}
								, #{reason}
							)
