mapper.select:

   select reply_num, num, reply_content, nickName, userId, replydate, updatedate, likeCnt ,dislikeCnt,money,parent_num, order_step, reply_level
	from 
  (
   select ROW_NUMBER() OVER(ORDER BY parent_num ASC, order_step ASC) AS rn, reply_num, num, reply_content, nickName, userId, replydate, updatedate, likeCnt ,dislikeCnt,money, parent_num, order_step, reply_level
   from
   DK_REPLY  
   where num = #{num} 
  ) where rn <= #{cri.pageNum} * #{cri.amount} and rn > (#{cri.pageNum} -1) * #{cri.amount}
